	   /*Create inventory transaction*/
	  /* select cstCreateInvTransactionForCostUpdate(cstItemCostHeaderId) into new_inv_trnx_id; */
	 
		 if(cstItemCostHeaderId > 0) then
		  /*Update Frozen cost*/
			 INSERT INTO cst_frozen_cost_header (inv_org_id, inv_item_master_id, cst_item_cost_header_id, amount, cost_details)  VALUES
			 (inv_org_id_val, inv_item_master_id_val, cst_item_cost_header_id_val, vv_total_amount_val, cost_details_val);
			 
			 SELECT LAST_INSERT_ID() into cstFrozenCostHeaderId;
			  if(cstFrozenCostHeaderId > 0 ) then
				  INSERT INTO cst_frozen_cost_line (cst_frozen_cost_header_id, cst_item_cost_line_id, cst_cost_element_id, amount, cost_basis, this_level_cb)  
