CREATE DEFINER=`root`@`localhost` FUNCTION `glReverseJournal`( reverseType varchar(50),   glJournalHeaderId int) RETURNS text CHARSET utf8mb4
BEGIN
declare newGlPeriod int;
declare newGlJournalHeaderId int;
declare adjustmentstatus varchar(50);
declare existingDocStatus varchar(50);
declare existingAdjustmentStatus varchar(50);
declare retMessage text;

SET retMessage = "Status of reverse process is unkown.Contact admin.";

SELECT gjh.doc_status, gjs.adjustment_status  into existingDocStatus, existingAdjustmentStatus 
from gl_journal_header gjh, gl_journal_header_status gjs
where gjh.gl_journal_header_id = glJournalHeaderId AND gjh.gl_journal_header_id = gjs.gl_journal_header_id LIMIT 1 ;

if existingDocStatus = "POSTED" && existingAdjustmentStatus = "ORIGINAL" then
	SELECT gl_period_id into newGlPeriod from gl_current_open_period_v where gl_ledger_id 
	IN (SELECT gl_ledger_id from gl_journal_header where gl_journal_header_id = glJournalHeaderId ) ;

	/*Create Journal Header*/
	INSERT INTO `inoerp`.`gl_journal_header` (`src_entity_name`, `src_entity_id`, `gl_ledger_id`, `gl_period_id`, `journal_source`,
	 `journal_category`, `journal_name`, `description`, `balance_type`, `reference_type`, `reference_key_name`, `reference_key_value`) 
	SELECT `src_entity_name`, `src_entity_id`, `gl_ledger_id`, newGlPeriod, `journal_source`, `journal_category`, `journal_name`,
	 `description`, `balance_type`, 'gl_journal_header', 'gl_journal_header_id', glJournalHeaderId
	FROM `inoerp`.`gl_journal_header` WHERE gl_journal_header_id = glJournalHeaderId ;

	SELECT last_insert_id() into newGlJournalHeaderId;

	/*Create Journal Lines*/
	if(newGlJournalHeaderId > 0 ) then
		if(reverseType = 'reverse_sign' ) then
			set adjustmentstatus = 'REVERSED_SIGN';
			INSERT INTO `inoerp`.`gl_journal_line` (`gl_journal_header_id`, `reference_entity_name`, `reference_key_name`, `reference_key_value`, `src_entity_name`, 
			`src_entity_id`, `p_src_entity_name`, `p_src_entity_id_name`, `line_num`, `p_src_entity_id`, `gl_ac_id`, `amount`, `dr_cr`, `gl_ledger_id`, 
			`gl_period_id`, `g_src_entity_name`, `g_src_entity_id_name`, `g_src_entity_id`, `ledger_amount`, `doc_currency`, `exchange_rate`)
			SELECT newGlJournalHeaderId, `reference_entity_name`, `reference_key_name`, `reference_key_value`, `src_entity_name`, 
			`src_entity_id`, `p_src_entity_name`, `p_src_entity_id_name`, `line_num`, `p_src_entity_id`, `gl_ac_id`, - amount,  
			`dr_cr`, `gl_ledger_id`, `gl_period_id`, `g_src_entity_name`, `g_src_entity_id_name`, `g_src_entity_id`, 
			-ledger_amount, `doc_currency`, `exchange_rate`
			FROM gl_journal_line WHERE gl_journal_header_id = glJournalHeaderId;
		else 
			set adjustmentstatus = 'REVERSED_DRCR';
			INSERT INTO `inoerp`.`gl_journal_line` (`gl_journal_header_id`, `reference_entity_name`, `reference_key_name`, `reference_key_value`, `src_entity_name`, 
			`src_entity_id`, `p_src_entity_name`, `p_src_entity_id_name`, `line_num`, `p_src_entity_id`, `gl_ac_id`, `amount`, `dr_cr`, `gl_ledger_id`, 
			`gl_period_id`, `g_src_entity_name`, `g_src_entity_id_name`, `g_src_entity_id`, `ledger_amount`, `doc_currency`, `exchange_rate`)
			SELECT newGlJournalHeaderId, `reference_entity_name`, `reference_key_name`, `reference_key_value`, `src_entity_name`, 
			`src_entity_id`, `p_src_entity_name`, `p_src_entity_id_name`, `line_num`, `p_src_entity_id`, `gl_ac_id`, `amount`, 
			case dr_cr
			WHEN 'D' THEN 'C'
			WHEN 'C' THEN 'D'
			END as dr_cr
			 , `gl_ledger_id`, 
			`gl_period_id`, `g_src_entity_name`, `g_src_entity_id_name`, `g_src_entity_id`, `ledger_amount`, `doc_currency`, `exchange_rate`
			FROM gl_journal_line WHERE gl_journal_header_id = glJournalHeaderId;
		end if;
		
		/*UPDATE source journal header status*/
		UPDATE `inoerp`.`gl_journal_header_status` SET `adjustment_status` =  adjustmentstatus WHERE (`gl_journal_header_id` = glJournalHeaderId);
        SET retMessage = "Journal is successfully reversed";
	else
		SET retMessage = "Failed to create reverse journal.";
	end if;
else
	SET retMessage = "Cant reverse the journal. Invalid status.";
end if;

RETURN retMessage;
END ;;
