CREATE DEFINER=`root`@`localhost` FUNCTION `wipUpdateWoLineQuantity`(
                                    fromWipWoLineId       INT ,
                                    toWipWoLineId         INT ,
                                    fromOperationStep     VARCHAR(30) ,
                                    toOperationStep       VARCHAR(30) ,
                                    moveQuantity          DECIMAL
                                    ) RETURNS int
BEGIN
  DECLARE   fromColumnName VARCHAR(100);
  DECLARE   newFromQty DECIMAL;
  DECLARE   toColumnName VARCHAR(100);
  DECLARE   newToQty DECIMAL;
  /*update from ops quantity*/
   
  
  IF(fromOperationStep = 'queue') THEN
  SELECT (ifnull(queue_quantity, 0) - moveQuantity) INTO newFromQty FROM wip_wo_line   WHERE    wip_wo_line_id = fromWipWoLineId;
   UPDATE    wip_wo_line   SET queue_quantity = newFromQty   WHERE    wip_wo_line_id = fromWipWoLineId   ;
  ELSE  IF(fromOperationStep = 'running') THEN
    SELECT (ifnull(running_quantity, 0) - moveQuantity) INTO newFromQty FROM wip_wo_line   WHERE    wip_wo_line_id = fromWipWoLineId;
      UPDATE    wip_wo_line   SET running_quantity = newFromQty   WHERE    wip_wo_line_id = fromWipWoLineId   ;
   ELSE  IF(fromOperationStep = 'completed') THEN
     SELECT (ifnull(completed_quantity, 0) - moveQuantity) INTO newFromQty FROM wip_wo_line   WHERE    wip_wo_line_id = fromWipWoLineId;
       UPDATE    wip_wo_line   SET completed_quantity = newFromQty   WHERE    wip_wo_line_id = fromWipWoLineId   ;
    ELSE  IF(fromOperationStep = 'rejected') THEN
      SELECT (ifnull(rejected_quantity, 0) - moveQuantity) INTO newFromQty FROM wip_wo_line   WHERE    wip_wo_line_id = fromWipWoLineId;
        UPDATE    wip_wo_line   SET rejected_quantity = newFromQty   WHERE    wip_wo_line_id = fromWipWoLineId   ;
     ELSE  RETURN 0;
     END IF;
    END IF;
   END IF;
  END IF;
 
  /*update to ops quantity*/
  IF(toOperationStep = 'scrapped') THEN
   SELECT  (ifnull(scrapped_quantity, 0) + moveQuantity)   INTO    newToQty    FROM wip_wo_line   WHERE    wip_wo_line_id = toWipWoLineId   ;
   UPDATE    wip_wo_line   SET scrapped_quantity = newToQty   WHERE    wip_wo_line_id = toWipWoLineId   ;
  ELSE  IF(toOperationStep = 'running') THEN
     SELECT  (ifnull(running_quantity, 0) + moveQuantity)   INTO    newToQty    FROM wip_wo_line   WHERE    wip_wo_line_id = toWipWoLineId   ;
      UPDATE    wip_wo_line   SET running_quantity = newToQty   WHERE    wip_wo_line_id = toWipWoLineId   ;
   ELSE  IF(toOperationStep = 'completed') THEN
      SELECT  (ifnull(completed_quantity, 0) + moveQuantity)   INTO    newToQty    FROM wip_wo_line   WHERE    wip_wo_line_id = toWipWoLineId   ;
       UPDATE    wip_wo_line   SET completed_quantity = newToQty   WHERE    wip_wo_line_id = toWipWoLineId   ;
    ELSE  IF(toOperationStep = 'rejected') THEN
       SELECT  (ifnull(rejected_quantity, 0) + moveQuantity)   INTO    newToQty    FROM wip_wo_line   WHERE    wip_wo_line_id = toWipWoLineId   ;
        UPDATE    wip_wo_line   SET rejected_quantity = newToQty   WHERE    wip_wo_line_id = toWipWoLineId   ;
     ELSE   
        SELECT  (ifnull(queue_quantity, 0) + moveQuantity)   INTO    newToQty    FROM wip_wo_line   WHERE    wip_wo_line_id = toWipWoLineId   ;
     UPDATE    wip_wo_line   SET queue_quantity = newToQty   WHERE    wip_wo_line_id = toWipWoLineId   ;
     END IF;
    END IF;
   END IF;
  END IF;
 
  
  RETURN 1;
 END ;;
